Chars =  require "chars"local M ={}local whallReplics = {"do not", "no no no", current = 1}local Filler = {}setmetatable(Filler, NameDescription)M.player = {symbol = "@", color = "cyan"}setmetatable(M.player, Filler)function M.player:interaction(filler)    --print("interaction")    if(filler.type == "whall") then        print(whallReplics[whallReplics.current])        whallReplics.current = 3 - whallReplics.current     endendfunction M.createFiller(symbol, color, unitType)    return setmetatable({["symbol"] = symbol, ["color"] = color, type = unitType}, Filler)endreturn M